package ArchitectureMVC1;import javax.swing.*;public class CourseController {    private CourseModel courseModel;    private CourseView courseView;    public CourseController(CourseModel courseModel, CourseView courseView){        this.courseModel = courseModel;        this.courseView = courseView;    }    public void setCourseName(String courseName){        if(!courseName.isEmpty()){            courseModel.setCourseName(courseName);        }        else {            JOptionPane.showMessageDialog(null,"Course name is empty.");        }    }    public void setCourseId(int courseId){        if(courseId > 0){            courseModel.setCourseId(courseId);        }        else{            JOptionPane.showMessageDialog(null, "Invalid course id.");        }    }    public void setCourseCategory(String courseCategory){        if(!courseCategory.isEmpty()){            courseModel.setCourseCategory(courseCategory);        }        else{            JOptionPane.showMessageDialog(null, "Course category is empty.");        }    }    public final int getCourseId(){        return courseModel.getCourseId();    }    public final String getCourseName(){        return courseModel.getCourseName();    }    public final String getCourseCategory(){        return courseModel.getCourseCategory();    }    public void updateView(){        courseView.courseDetails(courseModel.getCourseId(), courseModel.getCourseName(), courseModel.getCourseCategory());    }}