package ArchitectureMVC1;public class CourseModel {    private String courseName;    private int courseId;    private String courseCategory;    public CourseModel(){        courseId = 0;        courseName = null;        courseCategory = null;    }    public CourseModel(int courseId){        this();        this.courseId = courseId;    }    public CourseModel(int courseId, String courseName){        this(courseId);        this.courseName = courseName;    }    public CourseModel(int courseId, String courseName, String courseCategory){        this(courseId, courseName);        this.courseCategory = courseCategory;    }    public void setCourseId(int courseId){        this.courseId = courseId;    }    public final int getCourseId(){        return courseId;    }    public void setCourseName(String courseName){        this.courseName = courseName;    }    public final String getCourseName(){        return courseName;    }    public void setCourseCategory(String courseCategory){        this.courseCategory = courseCategory;    }    public final String getCourseCategory(){        return courseCategory;    }}