package ArchitectureMVC1; /*Model View Controller*/import org.jetbrains.annotations.NotNull;public class MainMVC {    public static void main(String[] args) {        CourseModel courseModel = retrieveDataFromDataBase();        CourseView courseView = new CourseView();        CourseController courseController = new CourseController(courseModel, courseView);        courseController.updateView();        courseController.setCourseName("ASP.NET");        courseController.updateView();    }    @NotNull    static CourseModel retrieveDataFromDataBase(){        CourseModel courseModel = new CourseModel();        courseModel.setCourseId(2001);        courseModel.setCourseName("Mastering Java");        courseModel.setCourseCategory("Programming");        return courseModel;    }}